CREATE DATABASE mysite;

USE mysite;

DROP TABLE IF EXISTS `dept`;
CREATE TABLE `dept` (
  `dept_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '部门ID',
  `name` varchar(10) NOT NULL DEFAULT '' COMMENT '部门名称',
  `dept_code` varchar(5) NOT NULL DEFAULT '' COMMENT '部门编码',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`dept_id`),
  UNIQUE KEY `udx$name` (`name`),
  UNIQUE KEY `udx$dept_code` (`dept_code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='部门表';


DROP TABLE IF EXISTS `employee`;
CREATE TABLE `employee` (
  `employee_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '员工ID',
  `name` varchar(10) NOT NULL DEFAULT '' COMMENT '员工姓名',
  `age` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '员工年龄',
  `gender` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '性别: 1、男，2、女、3、其他',
  `dept_id` bigint(20) unsigned NOT NULL COMMENT '部门ID',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`employee_id`),
  UNIQUE KEY `udx$name` (`name`),
  KEY `idx$dept_id` (`dept_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='员工表';
    
